package main

import "fmt"

func main() {
	fmt.Println(Day1(input))
}

// Day1 ...
func Day1(input string) int {
	var floor int
	for i := 0; i < len(input); i++ {
		if input[i] == '(' {
			floor++
			continue
		}
		floor--
	}
	return floor
}

